productos = [
    {"id": 1, "nombre": "Manzanaas", "descripcion": "Fruta dulce y jugosa.", "imagen": "https://upload.wikimedia.org/wikipedia/commons/thumb/1/15/Red_Apple.jpg/265px-Red_Apple.jpg"},
    {"id": 2, "nombre": "Plátano", "descripcion": "Fruta tropical y energética.", "imagen": "https://upload.wikimedia.org/wikipedia/commons/thumb/8/8a/Banana-Single.jpg/2560px-Banana-Single.jpg"},
    {"id": 3, "nombre": "Fresa", "descripcion": "Pequeña y deliciosa.", "imagen": "data:image/jpeg;base64,"},
    {"id": 4, "nombre": "Naranja", "descripcion": "Cítrica y refrescante.", "imagen": "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c4/Orange-Fruit-Pieces.jpg/1280px-Orange-Fruit-Pieces.jpg"},
    {"id": 5, "nombre": "Uva", "descripcion": "Pequeños racimos de dulzura.", "imagen": "data:image/jpeg;base64,"},
    {"id": 6, "nombre": "Mango", "descripcion": "Tropical y jugoso.", "imagen": "data:image/jpeg;base64,"},
    {"id": 7, "nombre": "Piña", "descripcion": "Fresca y exótica.", "imagen": "data:image/jpeg;base64,"},
    {"id": 8, "nombre": "Sandía", "descripcion": "Refrescante y jugosa.", "imagen": "data:image/jpeg;base64,"},
]

# Ruta para obtener la lista de productos
@app.route('/api/productos', methods=['GET'])
def obtener_productos():
    return jsonify(productos)

# Ruta para servir la página HTML
@app.route('/productos')
def index():
    return render_template('index.html', productos=productos)

if __name__ == '__main__':
    app.run(debug=True)
